import React, { useEffect, useRef } from 'react';
import Quill from 'quill';

interface TextboxProps {
  data: {
    ID: string;
    x: number;
    y: number;
    h: number;
    w: number;
  };
}

const Textbox: React.FC<TextboxProps> = ({ data }) => {
  const editorRef = useRef<HTMLDivElement>(null);
  
  useEffect(() => {
    if (editorRef.current) {
      new Quill(editorRef.current, {
        theme: 'snow',
        // Define your toolbar options here
      });
    }
  }, []);

  // Convert the percentage-based dimensions and positions to actual values if necessary
  const style = {
    position: 'absolute' as const,
    left: `${data.x}%`,
    top: `${data.y}%`,
    width: `${data.w}%`,
    height: `${data.h}%`,
    border: '1px solid black',
    padding: '10px',
  };

  return <div ref={editorRef} style={style}></div>;
};

export default Textbox;
