import React, { useEffect, useRef } from 'react';
import Quill from 'quill';

const Textbox = ({ data, isActive, setActiveTextboxId, containerDimensions }) => {
    const editorRef = useRef(null);

    useEffect(() => {
        if (editorRef.current) {
            const quill = new Quill(editorRef.current, {
                theme: 'snow',
                modules: {
                    toolbar: isActive ? '#toolbar' : false, // Use the global toolbar when active
                },
            });

            // Optional: if you want to focus on this editor upon creation
            if (isActive) quill.focus();
        }
    }, [isActive]);

    const handleClick = () => setActiveTextboxId(data.ID);

    // Style adjustments as needed
    const style: React.CSSProperties = {
        position: 'absolute',
        left: `${(data.y / 12) * containerDimensions.width}px`,
        top: `${(data.x / 16) * containerDimensions.height}px`,
        width: `${(data.w / 12) * containerDimensions.width}px`,
        height: `${(data.h / 16) * containerDimensions.height}px`,
        border: '1px solid black',
        padding: '10px',
    };

    return <div ref={editorRef} style={style} onClick={handleClick}></div>;
};

export default Textbox;
