import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import Textbox from './Textbox.tsx';
import Imagebox from './Imagebox.tsx';
import Videobox from './Videobox.tsx';
import Toolbar from './Toolbar.tsx'; // Assuming you have a Toolbar component
import { ArduinoData } from './types'; // Your type definitions

const App = () => {
    const [arduinoDataArray, setArduinoDataArray] = useState<ArduinoData[]>([]);
    const [activeTextboxId, setActiveTextboxId] = useState<string | null>(null);

    const [containerDimensions, setContainerDimensions] = useState({ width: 0, height: 0 });
    const containerRef = useRef<HTMLDivElement>(null); // Add this line

    useEffect(() => {
        if (containerRef.current) {
            const { width } = containerRef.current.getBoundingClientRect();
            const height = (4 / 3) * width; // Calculate height as 4/3 times the width
            setContainerDimensions({ width, height });
        }
    }, []); // This effect hook runs once after the component mounts

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/api/data`);
                const { data } = await response.json();
                if (data && data.length > 0) {
                    const newData = data.map((d) => JSON.parse(d)).map(parsed => ({
                        ...parsed,
                        x: Number(parsed.x),
                        y: Number(parsed.y),
                        h: Number(parsed.h),
                        w: Number(parsed.w),
                    }));
                    setArduinoDataArray(prevData => {
                        // Filter out any items to be deleted
                        const dataAfterDeletion = prevData.filter(prevItem => !newData.some(newItem => newItem.type === "delete" && newItem.ID === prevItem.ID));

                        // Add new items
                        const dataToAdd = newData.filter(newItem => newItem.type === "add").map(item => ({
                          ...item,
                          key: item.ID,
                        }));

                        return [...dataAfterDeletion, ...dataToAdd];
                      });
                }
            } catch (error) {
                console.error("Fetching data failed", error);
            }
        };

        const intervalId = setInterval(fetchData, 2000);
        return () => clearInterval(intervalId);
    }, []);


    return (
        <div className="App">
            <Toolbar activeTextboxId={activeTextboxId} />
            <div id="container" ref={containerRef}> {/* Add the ref here */}
                {arduinoDataArray.map((data) => {
                    switch (data.bracket) {
                        case 'text':
                            return (
                                <Textbox
                                    key={data.ID}
                                    data={data}
                                    isActive={data.ID === activeTextboxId}
                                    setActiveTextboxId={setActiveTextboxId}
                                    containerDimensions={containerDimensions}
                                />
                            );
                        case 'figure':
                            return <Imagebox key={data.ID} data={data} containerDimensions={containerDimensions} />;
                        case 'video':
                            return <Videobox key={data.ID} data={data} containerDimensions={containerDimensions} />;
                        default:
                            return null;
                    }
                })}
            </div>
        </div>
    );
};

export default App;
