import React, { useRef } from 'react';

const Videobox = ({ data, containerDimensions }) => {
    const inputRef = useRef(null);

    const handleChange = (event) => {
        // Handle the file upload logic here
        console.log(event.target.files[0]);
        // You might want to update the state or perform some action with the uploaded file
    };

    const style: React.CSSProperties = {
        position: 'absolute',
        left: `${(data.y / 12) * containerDimensions.width}px`,
        top: `${(data.x / 16) * containerDimensions.height}px`,
        width: `${(data.w / 12) * containerDimensions.width}px`,
        height: `${(data.h / 16) * containerDimensions.height}px`,
        border: '1px solid black',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    };

    return (
        <div style={style}>
            <input type="file" ref={inputRef} onChange={handleChange} style={{ display: 'none' }} accept="image/*" />
            <button onClick={() => inputRef.current.click()}>Upload Image</button>
        </div>
    );
};

export default Videobox;
