import React, { useEffect, useRef } from 'react';
import Quill from 'quill';

interface TextboxProps {
    data: {
        ID: string;
        x: number;
        y: number;
        h: number;
        w: number;
    };
}

const Textbox: React.FC<TextboxProps & { setActiveTextboxId: (id: string | null) => void }> = ({ data, setActiveTextboxId }) => {
    const editorRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        if (editorRef.current) {
            const quill = new Quill(editorRef.current, {
                theme: 'snow',
                // Your toolbar options
            });

            // Set this textbox as the active one when it's focused
            quill.on('selection-change', range => {
                if (range) {
                    setActiveTextboxId(data.ID);
                } else {
                    setActiveTextboxId(null);
                }
            });
        }
    }, [setActiveTextboxId, data.ID]);

    // Convert the percentage-based dimensions and positions to actual values if necessary
    const style = {
        position: 'absolute' as const,
        left: `${data.x}%`,
        top: `${data.y}%`,
        width: `${data.w}%`,
        height: `${data.h}%`,
        border: '1px solid black',
        padding: '10px',
    };

    return <div ref={editorRef} style={style}></div>;
};

export default Textbox;
