import React from 'react';

const Toolbar = ({ activeTextboxId }) => {
    const formatText = (command, value: string | undefined = undefined) => {
        document.execCommand(command, false, value);
    };

    return (
        <div id="toolbar" style={{ marginBottom: '20px' }}>
            <button style={buttonStyle} onClick={() => formatText('bold')}><b>Bold</b></button>
            <button style={buttonStyle} onClick={() => formatText('italic')}><i>Italic</i></button>
            <button style={buttonStyle} onClick={() => formatText('underline')}><u>Underline</u></button>
            <button style={buttonStyle} onClick={() => formatText('strikeThrough')}><s>Strike</s></button>
            <button style={buttonStyle} onClick={() => formatText('justifyLeft')}>Left</button>
            <button style={buttonStyle} onClick={() => formatText('justifyCenter')}>Center</button>
            <button style={buttonStyle} onClick={() => formatText('justifyRight')}>Right</button>
            <button style={buttonStyle} onClick={() => formatText('justifyFull')}>Justify</button>
            <button style={buttonStyle} onClick={() => formatText('insertOrderedList')}>OL</button>
            <button style={buttonStyle} onClick={() => formatText('insertUnorderedList')}>UL</button>
            {/* Add more buttons as needed */}
        </div>
    );
};

const buttonStyle = {
    cursor: 'pointer',
    background: '#f0f0f0',
    border: '1px solid #ddd',
    borderRadius: '5px',
    padding: '5px 10px',
    margin: '0 5px',
    fontSize: '16px',
};

export default Toolbar;
