import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import Textbox from './Textbox.tsx';
import Toolbar from './Toolbar.tsx'; // Assuming you have a Toolbar component
import { ArduinoData } from './types'; // Your type definitions

const App = () => {
    const [arduinoDataArray, setArduinoDataArray] = useState<ArduinoData[]>([]);
    const [activeTextboxId, setActiveTextboxId] = useState<string | null>(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/api/data`);
                const { data } = await response.json();
                if (data && data.length > 0) {
                    const newData = data.map((d) => JSON.parse(d)).map(parsed => ({
                        ...parsed,
                        x: Number(parsed.x),
                        y: Number(parsed.y),
                        h: Number(parsed.h),
                        w: Number(parsed.w),
                    }));
                    setArduinoDataArray(prevData => {
                        // Logic to add or remove textboxes based on type
                        return newData.reduce((acc, currData) => {
                            if (currData.type === "add") {
                                acc.push(currData); // Add new textbox data
                            } else if (currData.type === "delete") {
                                return acc.filter(item => item.ID !== currData.ID); // Remove textbox data
                            }
                            return acc;
                        }, [...prevData]);
                    });
                }
            } catch (error) {
                console.error("Fetching data failed", error);
            }
        };

        const intervalId = setInterval(fetchData, 2000);
        return () => clearInterval(intervalId);
    }, []);

    const containerRef = useRef(null);

    return (
        <div className="App">
            <Toolbar />
            <div id="container" ref={containerRef}>
                {arduinoDataArray.map((data) => (
                    <Textbox
                        key={data.ID}
                        data={data}
                        containerRef={containerRef} // Pass the ref here
                        isActive={data.ID === activeTextboxId}
                        setActiveTextboxId={setActiveTextboxId}
                    />
                ))}
            </div>
        </div>
    );
};

export default App;
