import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [arduinoData, setArduinoData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/data');
        const { data } = await response.json();
        console.log(data);
        setArduinoData(data);
      } catch (error) {
        console.error('Fetching data failed', error);
      }
    };

    const intervalId = setInterval(fetchData, 2000); // Poll every 2 seconds

    return () => clearInterval(intervalId); // Clean up
  }, []);

  return (
    <div className="App">
      <div id="toolbar">
        {/* Toolbar components here */}
      </div>
      <div id="container">
        {arduinoData ? <p>Data from Arduino: {arduinoData}</p> : <p>No data yet</p>}
      </div>
    </div>
  );
}

export default App;
