import React, { useState, useEffect } from "react";
import "./App.css";
import Textbox from "./Textbox";
import { ArduinoData } from './types';

function App() {
    const [arduinoDataArray, setArduinoDataArray] = useState<ArduinoData[]>([]);
    const [activeTextboxId, setActiveTextboxId] = useState<string | null>(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`http://localhost:3001/api/data`);
                const { data } = await response.json();
                if (data && data.length > 0) {
                    const newData = data.map((d) => JSON.parse(d)).map(parsed => ({
                        ...parsed,
                        x: Number(parsed.x),
                        y: Number(parsed.y),
                        h: Number(parsed.h),
                        w: Number(parsed.w),
                    }));
                    setArduinoDataArray(prevData => {
                        // Logic to add or remove textboxes based on type
                        return newData.reduce((acc, currData) => {
                            if (currData.type === "add") {
                                acc.push(currData); // Add new textbox data
                            } else if (currData.type === "delete") {
                                return acc.filter(item => item.ID !== currData.ID); // Remove textbox data
                            }
                            return acc;
                        }, [...prevData]);
                    });
                }
            } catch (error) {
                console.error("Fetching data failed", error);
            }
        };

        const intervalId = setInterval(fetchData, 2000);
        return () => clearInterval(intervalId);
    }, []);

    const handleTextboxClick = (id: string) => {
        setActiveTextboxId(id);
    };

    return (
        <div className="App">
            {/* Conditionally render toolbar if there's an active textbox */}
            {activeTextboxId && <div id="toolbar" />}
            <div id="container">
                {arduinoDataArray.map((data, index) => (
                    <Textbox key={data.ID} data={data} onClick={() => handleTextboxClick(data.ID)} isActive={data.ID === activeTextboxId} />
                ))}
            </div>
        </div>
    );
}

export default App;
