import React, { useState, useEffect } from 'react';
import './App.css';

interface ArduinoData {
  ID: string;
  type: string;
  bracket: string;
  x: string;
  y: string;
  h: string;
  w: string;
  touch: string;
}

function App() {
  const [arduinoDataArray, setArduinoDataArray] = useState<ArduinoData[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/data');
        const text = await response.text(); // Get the response as text
        console.log(text);

        if (text) { // Check if the text is not empty
          const data = JSON.parse(text); // Safely parse the text as JSON
          console.log(data);

          // Assuming 'data' is the structure you expect
          if (data && data.ID) { // Further validation if necessary
            setArduinoDataArray(prevData => [...prevData, data]);
          }
        }
      } catch (error) {
        console.error('Fetching data failed', error);
      }
    };

    const intervalId = setInterval(fetchData, 2000); // Poll every 2 seconds

    return () => clearInterval(intervalId); // Clean up on unmount
  }, []);  

  return (
    <div className="App">
      <div id="toolbar">
        {/* Toolbar components here */}
      </div>
      <div id="container">
        {arduinoDataArray.map((data, index) => (
          <p key={index}>Data from Arduino: {JSON.stringify(data)}</p>
        ))}
      </div>
    </div>
  );
}

export default App;
