import React, { useState, useEffect } from 'react';
import './App.css';
import Textbox from './Textbox.tsx'; // Import the Textbox component

interface ArduinoData {
  ID: string;
  type: string;
  bracket: string;
  x: string;
  y: string;
  h: string;
  w: string;
  touch: string;
}

function App() {
  const [arduinoDataArray, setArduinoDataArray] = useState<ArduinoData[]>([]);
  const [lastId, setLastId] = useState(0); // New state for tracking last message ID

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:3001/api/data?lastId=${lastId}`);
        const { data, lastId: newLastId } = await response.json(); // Destructure newLastId from response
        console.log(data);

        if (data && data.length > 0) { // Check if there are new messages
          const newData = data.map(d => JSON.parse(d)); // Assuming data is an array of JSON strings
          setArduinoDataArray(prevData => [...prevData, ...newData]);
          setLastId(newLastId); // Update lastId with the new lastId from the server
        }
      } catch (error) {
        console.error('Fetching data failed', error);
      }
    };

    const intervalId = setInterval(fetchData, 2000); // Poll every 2 seconds

    return () => clearInterval(intervalId); // Clean up on unmount
  }, [lastId]); // Add lastId as a dependency

  return (
    <div className="App">
      <div id="toolbar">
        {/* Toolbar components here */}
      </div>
      <div id="container">
        {arduinoDataArray.map((data, index) => (
          <Textbox key={index} data={data} /> // Render a Textbox for each set of data
        ))}
      </div>
    </div>
  );
}

export default App;
