import React, { useEffect, useRef } from 'react';
import Quill from 'quill';
import { ArduinoData } from './types';

interface TextboxProps {
  data: ArduinoData;
  isActive: boolean;
  onClick: () => void;
}

const Textbox: React.FC<TextboxProps> = ({ data, isActive, onClick }) => {
  const editorRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (editorRef.current && isActive) {
      // Initialize Quill for the active textbox
      new Quill(editorRef.current, {
        theme: 'snow',
        modules: {
          toolbar: '#toolbar'
        }
      });
    }
  }, [isActive]);

  return (
    <div onClick={onClick} style={{ position: 'absolute', left: `${data.x}%`, top: `${data.y}%`, width: `${data.w}%`, height: `${data.h}%`, border: '1px solid black' }} ref={editorRef}></div>
  );
};

export default Textbox;
